package ConditionalStatements;

public class Elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a=10;

		if(a<5)
		{
			
			System.out.println("a is less then 5");
			
		}
		else if(a>10 && a<20)
		{
			
			System.out.println("a is greater then 5 and it is in the range of 10 and 20");
			
		}
		else
		{
			System.out.println("a is greater than 5 and it is not in the range of 10 and 20");
			
		}
		
		

	}

}
