package ConditionalStatements;

public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;

		if(a<5)
		{
			
			System.out.println("a is less then 5");
			
		}
		else
		{
			
			System.out.println("a is greater then 5");
			
		}
		
		
		

	}

}
