package Strings;

public class MethodContains {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		

		String str="India is great country";
		
		
		//Syntax:--Stringvariable.contains('character' or'String');
		//Return:-It will verify wether the user given character or string is present or not in the source string
		//Return Type:-boolean
		
		
		if (str.contains("great"))
		{
			System.out.println("The user given word is available");
			
		}
		else
		{
			System.out.println("The user given word is not available");
			
		}
		

	}

}
