package Strings;

public class MethodLastindexOf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String str="India is great country";
		
		
		//Syntax:--Stringvariable.lastIndexOf('Character');
		//Return:-It will return the charcater for given position
		//Return Type:-character
//Note:it wil take only the last occurance of a character in the given string
		
System.out.println("The Last occurance of a given string is"+str.lastIndexOf('I'));
	}

}
