package Strings;

public class MethodReplace {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
String str="India is great country";
		
		
		//Syntax:--Stringvariable.replace("find value","Replace value");
		//Return:-It will replace the user given value
		//Return Type:-string



String replacestring=str.replace(" ", "@");

System.out.println(replacestring);
		
		
		
	}

}
