package StringsandCommonExamples;

public class GeneratingPatternwithSpecialSymbol {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char c='*';
		
		String temp="";
		
		
		for(int i=1;i<=10;i++)
		{
			
			temp=temp+c;
			
			System.out.println(temp);
			
			
		}
		
		
		
		

	}

}
